import React from "react";


const PrimeNumbersView = (): JSX.Element =>  {
    const [upperBound, setUpperBound] = React.useState(
        {value:10, valid:true}
    );
    const  handleSubmit = async (event:{preventDefault:() => void}) => {

        event.preventDefault()
        console.log("fetch");
        //try fetch
        const response = await window.fetch(
            "http://localhost:8080/primes?upperBound="+ upperBound
        );
        console.log("fetch");
        if(!response.ok){
            alert("error fetching");
            return;
        }

        let data = await response.json();
        console.log(data);
        data = {
            ...data,
            multiplesLists: data.multiplesLists
                            .filter((arr: number[]) => arr.length > 0)
        };

        console.log(data);
    };

    return (
      <form onSubmit={handleSubmit}>
        <input type="text" name="upperBound" onChange={(e: any) => setUpperBound({value:e.target.value, valid:e.target.value >= 2})}/>
        <input type="submit" />
      </form>
    );
};
export default PrimeNumbersView;
